export declare type HslColor = {
    h: number;
    s: number;
    l: number;
    a: number;
};
export declare type HsvColor = {
    h: number;
    s: number;
    v: number;
    a: number;
};
export declare type RgbColor = {
    r: number;
    g: number;
    b: number;
    a: number;
};
export declare type Alpha = {
    a: number;
};
export declare type Color = string | RgbColor | HslColor | HsvColor;
export declare type ColorObject = {
    hex: string;
    rgb: RgbColor;
    hsl: HslColor;
    hsv: HsvColor;
    alpha: number;
};
export declare type ColorCombination = "analogous" | "monochromatic" | "splitcomplement" | "triad" | "tetrad" | "complement";
export declare type Theme = {
    background?: string;
    inputBackground?: string;
    color?: string;
    borderColor?: string;
    borderRadius?: string;
    boxShadow?: string;
    width?: string;
};
